mapper.select:
select b.ifcdSeq ,b.ifcdName ,b.ifcdUseNy ,b.ifcdOrder ,b.ifcdDesc ,b.ifcdDatetime ,b.ifcdDatetimeSvr ,b.ifcdDelNy ,b.codegroup_ifcgSeq ,a.ifcgName from code b left join codegroup a on a.ifcgSeq = b.codegroup_ifcgSeq where 1=1 AND ifcdDelNy = #{shDelNy} AND ifcdUseNy = #{shUseNy}
AND ifcdSeq = #{shValue} AND ifcdName LIKE CONCAT('%',#{shValue},'%') AND codegroup_ifcgSeq = #{shValue} AND ifcgName LIKE CONCAT('%',#{shValue},'%')
order by b.ifcdSeq desc

mapper.update:
update code
set
	ifcdName = #{ifcdName}
	,ifcdUseNy = #{ifcdUseNy}
	,ifcdOrder = #{ifcdOrder}
	,ifcdDesc = #{ifcdDesc}
	,ifcdDatetime = now()
	,ifcdDatetimeSvr = now()
	,ifcdDelNy = #{ifcdDelNy}
where 1=1
	and ifcdSeq = #{ifcdSeq}

mapper.delete:
	delete from code
  	where 1=1
  		and ifcdSeq = #{ifcdSeq}
