mapper.sql:
from
	member a
where 1=1
	AND delNy = #{shDelNy}
	AND gender = #{shGender}


		AND a.memberDatetime BETWEEN #{shDateStart} AND #{shDateEnd}
		AND a.memberDatetimeSvr BETWEEN #{shDateStart} AND #{shDateEnd}



		AND seq = #{shValue}
		AND name LIKE CONCAT('%',#{shValue},'%')

mapper.update:
	update member
  	set
		name = #{name}
	    ,nickName = #{nickName}
	    ,id = #{id}
	    ,password = #{password}
	    ,checkingpassword = #{checkingpassword}
	    ,email = #{email}
	    ,birthday = #{birthday}
	    ,gender = #{gender}
	    ,telephone = #{telephone}
	    ,memberDatetime = now()
	    ,memberDatetimeSvr = now()
	    ,delNy = #{delNy}
  	where 1=1
  		and seq = #{seq}

mapper.delete:
	delete from member
  	where 1=1
  		and seq = #{seq}
