mapper.sql:
from
	productcd b left join productcg a on a.pdSeq = b.productCg_pdSeq
where 1=1
	AND pddDelNy = #{shDelNy}
	AND pddUseNy = #{shUseNy}


		AND b.pddDatetime BETWEEN #{shDateStart} AND #{shDateEnd}
		AND b.pddDatetimeSvr BETWEEN #{shDateStart} AND #{shDateEnd}



		AND pddSeq = #{shValue}
		AND pddName LIKE CONCAT('%',#{shValue},'%')
		AND productCg_pdSeq = #{shValue}
		AND pddCategory LIKE CONCAT('%',#{shValue},'%')

mapper.insert:
  insert into productcd (
	pddName
	,pddCategory
    ,pddUseNy
    ,pddOrder
    ,pddDesc
    ,pddPrice
    ,pddsales
    ,pddSalesPrice
    ,pddCount
    ,pddTotalPrice
    ,pddDatetime
    ,pddDatetimeSvr
    ,pddDelNy
    ,pddSalesNy
    ,pddDatetimeRel
	,pddProgramer
	,pddDistributor
	,pddPlus
	,pddWish
	,pddDetail1
	,pddDetail2
	,pddDetail3
	,pddDetail4
	,pddDetail5
	,pddDetail6
	,pddDetail7
	,pddDetail8
	,pddDetail9
	,pddDetail10
	,pddDetail11
	,pddDetail12
	,pddDetail13
	,pddDetail14
	,pddDetail15
	,pddDetail16
    ,productCg_pdSeq
) values (
	#{pddName}
	,#{pddCategory}
    ,#{pddUseNy}
    ,#{pddOrder}
    ,#{pddDesc}
    ,#{pddPrice}
    ,#{pddsales}
    ,#{pddSalesPrice}
    ,#{pddCount}
    ,#{pddTotalPrice}
    ,now()
    ,now()
    ,0
    ,#{pddSalesNy}
    ,now()
    ,#{pddProgramer}
    ,#{pddDistributor}
    ,#{pddPlus}
    ,#{pddWish}
    ,#{pddDetail1}
    ,#{pddDetail2}
    ,#{pddDetail3}
    ,#{pddDetail4}
    ,#{pddDetail5}
    ,#{pddDetail6}
    ,#{pddDetail7}
   	,#{pddDetail8}
   	,#{pddDetail9}
   	,#{pddDetail10}
   	,#{pddDetail11}
    ,#{pddDetail12}
    ,#{pddDetail13}
   	,#{pddDetail14}
   	,#{pddDetail15}
   	,#{pddDetail16}
    ,#{pdSeq}
)

		SELECT last_insert_id()

mapper.update:
  	update productcd
  	set
  		pddName = #{pddName}
  		,pddCategory = #{pddCategory}
  		,pddUseNy = #{pddUseNy}
  		,pddOrder = #{pddOrder}
  		,pddDesc = #{pddDesc}
  		,pddPrice = #{pddPrice}
  		,pddsales = #{pddsales}
  		,pddSalesPrice = #{pddSalesPrice}
  		,pddCount = #{pddCount}
  		,pddSalesNy = #{pddSalesNy}
  		,pddDatetime = now()
  		,pddDatetimeSvr = now()
  		,pddProgramer = #{pddProgramer}
		,pddDistributor = #{pddDistributor}
		,pddDetail1 = #{pddDetail1}
		,pddDetail2 = #{pddDetail2}
		,pddDetail3 = #{pddDetail3}
		,pddDetail4 = #{pddDetail4}
		,pddDetail5 = #{pddDetail5}
		,pddDetail6 = #{pddDetail6}
		,pddDetail7 = #{pddDetail7}
		,pddDetail8 = #{pddDetail8}
		,pddDetail9 = #{pddDetail9}
		,pddDetail10 = #{pddDetail10}
		,pddDetail11 = #{pddDetail11}
		,pddDetail12 = #{pddDetail12}
		,pddDetail13 = #{pddDetail13}
		,pddDetail14 = #{pddDetail14}
		,pddDetail15 = #{pddDetail15}
		,pddDetail16 = #{pddDetail16}
  	where 1=1
  		and pddSeq = #{pddSeq}

mapper.delete:
	delete from productcd
  	where 1=1
  		and pddSeq = #{pddSeq}
