mapper.select:
select a.pdSeq ,a.pdCategory ,a.pdUseNy ,a.pdOrder ,a.pdDesc ,a.pdDatetime ,a.pdDatetimeSvr ,a.pdDelNy from productCg a where 1=1 AND pdDelNy = #{shDelNy} AND pdUseNy = #{shUseNy}
AND a.pdDatetime BETWEEN #{shDateStart} AND #{shDateEnd} AND a.pdDatetimeSvr BETWEEN #{shDateStart} AND #{shDateEnd}
AND pdSeq = #{shValue} AND pdCategory LIKE CONCAT('%',#{shValue},'%')
order by a.pdSeq desc

mapper.update:
	update productCg
  	set
		pdCategory = #{pdCategory}
  		,pdUseNy = #{pdUseNy}
  		,pdOrder = #{pdOrder}
  		,pdDesc = #{pdDesc}
  		,pdDatetime = now()
  		,pdDatetimeSvr = now()
  		,pdDelNy = #{pdDelNy}
  	where 1=1
  		and pdSeq = #{pdSeq}

mapper.delete:
	delete from productCg
  	where 1=1
  		and pdSeq = #{pdSeq}
