mapper.sql:
from
	pddPlayer d left join productCd b on b.pddSeq = d.productCd_pddSeq
where 1=1
	AND psDelNy = #{shDelNy}
	AND psUseNy = #{shUseNy}







		AND ppSeq = #{shValue}
		AND ppPlayer LIKE CONCAT('%',#{shValue},'%')
		AND productCd_pddSeq = #{shValue}

mapper.insert:
  insert into pddPlayer (
  	ppPlayer
	,ppUseNy
	,ppDelNy
    ,productCd_pddSeq
) values (
	#{ppPlayer}
	,#{ppUseNy}
    ,0
    ,#{pddSeq}
)

		SELECT last_insert_id()

mapper.update:
  	update pddPlayer
  	set
  		ppPlayer = #{ppPlayer}
		,ppUseNy = #{ppUseNy}
		,ppDelNy = #{ppDelNy}
  	where 1=1
  		and ppSeq = #{ppSeq}

mapper.delete:
	delete from pddPlayer
  	where 1=1
  		and ppSeq = #{ppSeq}
