mapper.sql:
from
	member a
where 1=1
	AND delNy = #{shDelNy}
	AND admNy = #{shAdmNy}


		AND a.memberDatetime BETWEEN #{shDateStart} AND #{shDateEnd}
		AND a.memberDatetimeSvr BETWEEN #{shDateStart} AND #{shDateEnd}



		AND seq = #{shValue}
		AND name LIKE CONCAT('%',#{shValue},'%')
		AND id LIKE CONCAT('%',#{shValue},'%')
		AND password LIKE CONCAT('%',#{shValue},'%')

mapper.insert:
insert into member (
	name
    ,nickName
    ,id
    ,password
    ,checkingpassword
    ,email
    ,birthday
    ,gender
    ,telephone
    ,memberDatetime
    ,memberDatetimeSvr
    ,admNy
    ,delNy
) values (
	#{name}
	,#{nickName}
    ,#{id}
    ,#{password}
    ,#{checkingpassword}
    ,#{email}
    ,#{birthday}
    ,#{gender}
    ,#{telephone}
    ,now()
    ,now()
    ,0
    ,0
)

		SELECT last_insert_id()

mapper.update:
	update member
  	set
		name = #{name}
	    ,nickName = #{nickName}
	    ,id = #{id}
	    ,password = #{password}
	    ,checkingpassword = #{checkingpassword}
	    ,email = #{email}
	    ,birthday = #{birthday}
	    ,gender = #{gender}
	    ,telephone = #{telephone}
	    ,memberDatetime = now()
	    ,memberDatetimeSvr = now()
	    ,admNy = #{admNy}
	    ,delNy = #{delNy}
  	where 1=1
  		and seq = #{seq}

mapper.delete:
	delete from member
  	where 1=1
  		and seq = #{seq}
