mapper.sql:
from
	productCd b left join productCg a on a.pdSeq = b.productCg_pdSeq
	where 1=1
	AND pddDelNy = #{shDelNy}
	AND pddUseNy = #{shUseNy}


		AND b.pddDatetime BETWEEN #{shDateStart} AND #{shDateEnd}
		AND b.pddDatetimeSvr BETWEEN #{shDateStart} AND #{shDateEnd}



		AND pddSeq = #{shValue}
		AND pddName LIKE CONCAT('%',#{shValue},'%')
		AND productCg_pdSeq = #{shValue}
		AND pddCategory LIKE CONCAT('%',#{shValue},'%')

mapper.insert:
  insert into pddReview (
	prName
	,prReview
	,prReviewDesc
	,prUseNy
	,prDelNy
	,prOrder
	,prDatetime
	,prDatetimeSvr
	,productCd_pddSeq
) values (
	#{prName}
	,#{prReview}
	,#{prReviewDesc}
    ,1
    ,0
    ,#{prOrder}
    ,now()
    ,now()
    ,#{productCd_pddSeq}
)

		SELECT last_insert_id()

mapper.update:
update productCd
set
	pddReview = #{pddReview}
	,pddReviewDesc = #{pddReviewDesc}
where 1=1
	and pddSeq = #{pddSeq}

update productCd
set
	pddPlus = #{pddPlus}
	,pddTotalPrice = #{pddTotalPrice}
where 1=1
	and pddSeq = #{pddSeq}

mapper.select:
        SELECT IFNULL(ROUND(AVG(prReview)),0) as prReviewAvg
		FROM pddReview
		WHERE productCd_pddSeq = #{pddSeq};

mapper.delete:
	delete from productCd
  	where 1=1
  		and pddSeq = #{pddSeq}

mapper.insert:
  insert into purchaseList (
	plImage
	,plName
	,plPrice
	,plCount
	,plTotalPrice
	,plDelNy
	,plDatetime
	,plDatetimeSvr
	,member_seq
) values (
	#{plImage}
	,#{plName}
	,#{plPrice}
    ,1
    ,#{plTotalPrice}
    ,0
    ,now()
    ,now()
    ,#{member_seq}
)

		SELECT last_insert_id()
