mapper.sql:
from
	productCd b left join productCg a on a.pdSeq = b.productCg_pdSeq
where 1=1
	AND pddDelNy = #{shDelNy}
	AND pddUseNy = #{shUseNy}


		AND b.pddDatetime BETWEEN #{shDateStart} AND #{shDateEnd}
		AND b.pddDatetimeSvr BETWEEN #{shDateStart} AND #{shDateEnd}



		AND pddSeq = #{shValue}
		AND pddName LIKE CONCAT('%',#{shValue},'%')
		AND productCg_pdSeq = #{shValue}
		AND pddCategory LIKE CONCAT('%',#{shValue},'%')

mapper.insert:
  insert into productCd (
	pddName
	,pddCategory
    ,pddUseNy
    ,pddOrder
    ,pddDesc
    ,pddPrice
    ,pddsales
    ,pddSalesPrice
    ,pddCount
    ,pddTotalPrice
    ,pddDatetime
    ,pddDatetimeSvr
    ,pddDelNy
    ,pddDatetimeRel 
	,pddProgramer 
	,pddDistributor
	,pddSystem  
	,pddDetail 
	,pddPlus 
	,pddWish 
    ,productCg_pdSeq
) values (
	#{pddName}
	,#{pddCategory}
    ,#{pddUseNy}
    ,#{pddOrder}
    ,#{pddDesc}
    ,#{pddPrice}
    ,#{pddsales}
    ,#{pddSalesPrice}
    ,#{pddCount}
    ,#{pddTotalPrice}
    ,now()
    ,now()
    ,0
    ,#{pddDatetimeRel}
    ,#{pddProgramer}
    ,#{pddDistributor}
    ,#{pddSystem}
    ,#{pddDetail}
    ,#{pddPlus}
    ,#{pddWish}
    ,#{pdSeq}
)

		SELECT last_insert_id()

mapper.update:
  	update productCd
  	set
  		pddName = #{pddName}
  		,pddCategory = #{pddCategory}
  		,pddUseNy = #{pddUseNy}
  		,pddOrder = #{pddOrder}
  		,pddDesc = #{pddDesc}
  		,pddPrice = #{pddPrice}
  		,pddsales = #{pddsales}
  		,pddSalesPrice = #{pddSalesPrice}
  		,pddCount = #{pddCount}
  		,pddTotalPrice = #{pddTotalPrice}
  		,pddDatetime = now()
  		,pddDatetimeSvr = now()
  		,pddReview = #{pddReview}
  		,pddReviewDesc = #{pddReviewDesc}
  		,pddDelNy = #{pddDelNy}
		,pddProgramer = #{pddProgramer}
		,pddDistributor = #{pddDistributor}
		,pddSystem = #{pddSystem}
		,pddDetail = #{pddDetail}
		,pddPlus = #{pddPlus}
		,pddWish = #{pddWish}
  	where 1=1
  		and pddSeq = #{pddSeq}

mapper.delete:
	delete from productCd
  	where 1=1
  		and pddSeq = #{pddSeq}
